package net.wanho.po.person;

import java.util.Date;

/**
 * 工作日志评论
 * @author Administrator
 *
 */
public class Comment {
	/**评论主键*/
	private Integer commentId;
	/**日志编码*/
	private Integer recordId;
	/**评论人*/
	private Integer commentemployee;
	/**评论内容*/
	private String detail;
	/**评论时间*/
	private Date createTime;
	public Comment() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Comment(Integer commentId, Integer recordId, Integer commentemployee, String detail, Date createTime) {
		super();
		this.commentId = commentId;
		this.recordId = recordId;
		this.commentemployee = commentemployee;
		this.detail = detail;
		this.createTime = createTime;
	}
	public Integer getCommentId() {
		return commentId;
	}
	public void setCommentId(Integer commentId) {
		this.commentId = commentId;
	}
	public Integer getRecordId() {
		return recordId;
	}
	public void setRecordId(Integer recordId) {
		this.recordId = recordId;
	}
	public Integer getCommentemployee() {
		return commentemployee;
	}
	public void setCommentemployee(Integer commentemployee) {
		this.commentemployee = commentemployee;
	}
	public String getDetail() {
		return detail;
	}
	public void setDetail(String detail) {
		this.detail = detail;
	}
	public Date getCreateTime() {
		return createTime;
	}
	public void setCreateTime(Date createTime) {
		this.createTime = createTime;
	}
	@Override
	public String toString() {
		return "Comment [commentId=" + commentId + ", recordId=" + recordId + ", commentemployee=" + commentemployee
				+ ", detail=" + detail + ", createTime=" + createTime + "]";
	}
	
	
}
